import Chart from "./../Chart/Chart";

const ExpensesChart = (props) => {
  const dataPoints = [
    { label: "Jan", value: 0 },
    { label: "Feb", value: 0 },
    { label: "Mar", value: 0 },
    { label: "Apr", value: 0 },
    { label: "May", value: 0 },
    { label: "Jun", value: 0 },
    { label: "Jul", value: 0 },
    { label: "Aug", value: 0 },
    { label: "Sep", value: 0 },
    { label: "Nov", value: 0 },
    { label: "Oct", value: 0 },
    { label: "Dec", value: 0 },
  ];
  for (const index in dataPoints) {
    console.log(i)
    let dataPointValue = dataPoints[index].value;
    console.log(dataPointValue, 'dataPointValue');
    let filterSameMonth = props.expenses.filter(
      (expense) => expense.date.getMonth == index
    );
    console.log(filterSameMonth, 'filterSameMonth')
    let filterAmounts = filterSameMonth.map((expense) => expense.amount);
    console.log(filterAmounts, 'filterAmounts')
    if (filterAmounts.length) {
      dataPointValue = filterAmounts.reduce((a, b) => a + b);
    }
    console.log(dataPointValue)
  }
  let dataPointValues = dataPoints.map((dataPoint) => dataPoint.value);
  let maxValue = Math.max(...dataPointValues);
  return (
    <div>
      <Chart
        dataPoints={dataPoints}
        expenses={props.expenses}
        maxValue={maxValue}
      />
    </div>
  );
};
export default ExpensesChart;
