import ExpenseForm from './ExpenseForm';
import './NewExpense.css';
import Card from "../UI/Card";
import { useState } from 'react';

const NewExpense = (props) =>{
    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData={
            ...enteredExpenseData,
            id: Math.random().toString()
        }
        props.onAddExpense(expenseData);
    }
    let [showForm, setShowForm] = useState('false');
    
    const toggleForm =()=>{
        setShowForm(()=>!showForm)
        console.log(showForm);
    }
    let expenseFormContent = <button onClick={toggleForm}>Add New Expense</button>;
    
    if(showForm==true ){
        expenseFormContent=<ExpenseForm onSaveExpenseData={saveExpenseDataHandler}/>
    }
    return(
        <Card className='new-expense'>
            {expenseFormContent}
            {/* {showForm ? (<ExpenseForm onSaveExpenseData={saveExpenseDataHandler}/>) :  */}
            (<button  onClick={toggleForm}>Add New Expense</button>)}
        </Card>
    )
}
export default NewExpense;
